"reconciliation-query-batch":
{
  "$schema": "http://json-schema.org/schema#",
  "$id": "https://reconciliation-api.github.io/specs/draft/schemas/reconciliation-query.json",
  "type": "object",
  "description": "This schema validates the JSON serialization of any reconciliation query batch, i.e. the payload of a POST to a reconciliation endpoint.",
  "definitions": {
    "property_value": {
      "oneOf": [
        {
          "type": "string"
        },
        {
          "type": "number"
        },
        {
          "type": "boolean"
        },
        {
          "type": "object",
          "description": "A property value which represents another entity, for instance if it was previously reconciled itself",
          "properties": {
            "id": {
              "type": "string"
            },
            "name": {
              "type": "string"
            }
          },
          "required": [
            "id"
          ]
        }
      ]
    }
  },
  "required": [
    "queries"
  ],
  "properties": {
    "queries": {
      "type": "array",
      "description": "A list of queries to be processed by the service",
      "items": {
        "type": "object",
        "properties": {
          "type": {
            "description": "A type identifier indicating which class of entities to restrict the search to",
            "type": "string"
          },
          "limit": {
            "type": "number",
            "description": "The maximum number of candidates to return"
          },
          "lang": {
            "type": "string",
            "description": "The text-processing language for the query"
          },
          "conditions": {
            "type": "array",
            "minItems": 1,
            "description": "A list of conditions to select candidates",
            "items": {
              "type": "object",
              "properties": {
                "matchType": {
                  "type": "string",
                  "description": "A string to indicate whether to match the supplied value to entity names or property values",
                  "enum": [
                    "name",
                    "property"
                  ]
                },
                "pid": {
                  "type": "string",
                  "description": "The identifier of the property, whose values will be compared to the values supplied. Required if 'matchType' is 'property'."
                },
                "v": {
                  "description": "A value (or array of values) to match against the entity names or property values associated with the property on each candidate",
                  "oneOf": [
                    {
                      "$ref": "#/definitions/property_value"
                    },
                    {
                      "type": "array",
                      "items": {
                        "$ref": "#/definitions/property_value"
                      }
                    }
                  ]
                },
                "required": {
                  "type": "boolean",
                  "description": "A boolean indicating if a match of this condition is required for an entity to enter the list of candidates"
                },
                "matchQuantifier": {
                  "type": "string",
                  "description": "A string to indicate which of the values in v to match",
                  "enum": [
                    "any",
                    "all",
                    "none"
                  ]
                },
                "matchQualifier": {
                  "type": "string",
                  "description": "A string to indicate how to match the values in v"
                }
              },
              "required": [
                "matchType",
                "v"
              ]
            }
          }
        },
        "required": [
          "conditions"
        ],
        "additionalProperties": false
      }
    }
  }
}
,
"reconciliation-result-batch":
{
  "$schema": "http://json-schema.org/schema#",
  "$id": "https://reconciliation-api.github.io/specs/draft/schemas/reconciliation-result-batch.json",
  "type": "object",
  "description": "This schema can be used to validate the JSONÂ serialization of any reconciliation result batch.",
  "required": [
    "results"
  ],
  "properties": {
    "results": {
      "type": "array",
      "description": "The list of candidates for each reconciliation query, in the same order",
      "items": {
        "type": "object",
        "properties": {
          "candidates": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "type": "string",
                  "description": "Entity identifier of the candidate"
                },
                "name": {
                  "type": "string",
                  "description": "Entity name of the candidate"
                },
                "description": {
                  "type": "string",
                  "description": "Optional description of the candidate entity"
                },
                "score": {
                  "type": "number",
                  "description": "Number indicating how likely it is that the candidate matches the query"
                },
                "features": {
                  "type": "array",
                  "description": "A list of features which can be used to derive a matching score",
                  "items": {
                    "type": "object",
                    "properties": {
                      "id": {
                        "type": "string",
                        "description": "A unique string identifier for the feature"
                      },
                      "name": {
                        "type": "string",
                        "description": "A human-readable name for the feature"
                      },
                      "value": {
                        "description": "The value of the feature for this reconciliation candidate",
                        "oneOf": [
                          {
                            "type": "boolean"
                          },
                          {
                            "type": "number"
                          }
                        ]
                      }
                    },
                    "required": [
                      "id",
                      "name",
                      "value"
                    ]
                  }
                },
                "match": {
                  "type": "boolean",
                  "description": "Boolean value indicating whether the candiate is a certain match or not."
                },
                "type": {
                  "type": "array",
                  "description": "Types the candidate entity belongs to",
                  "items": {
                    "oneOf": [
                      {
                        "type": "object",
                        "description": "A type can be given by id and name",
                        "properties": {
                          "id": {
                            "type": "string"
                          },
                          "name": {
                            "type": "string"
                          }
                        },
                        "required": [
                          "id"
                        ]
                      },
                      {
                        "type": "string",
                        "description": "Alternatively, if only a string is given, it is treated as the id"
                      }
                    ]
                  }
                }
              },
              "required": [
                "id",
                "name"
              ]
            }
          }
        },
        "required": [
          "candidates"
        ]
      }
    }
  }
}
